import React, { useState } from 'react';

const SurveyFormPage = () => {
    const [latexCode, setLatexCode] = useState('');
    const [pdfUrl, setPdfUrl] = useState(null);
    const [selectedOption, setSelectedOption] = useState('mid');

    const handleInputChange = (event) => {
        setLatexCode(event.target.value);
        setPdfUrl(null);
    };

    const handleOptionChange = (event) => {
        setSelectedOption(event.target.value);
    };

    const handleCompile = async () => {
        try {
            let rawLatex = '';
            if (selectedOption === 'mid') {
                rawLatex = await fetchTextFile('C://Users//SHAIK JAVED//OneDrive//Desktop/Miniproject\questgen\midqp.txt');
            } else {
                rawLatex = await fetchTextFile('C:\Users\SHAIK JAVED\OneDrive\Desktop\Miniproject\questgen\semqp.txt');
            }

            const response = await fetch('https://tinyurl.com/cmpillatex', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ latex: rawLatex }),
            });

            if (!response.ok) {
                throw new Error('Failed to compile LaTeX');
            }

            const blob = await response.blob();
            const url = URL.createObjectURL(blob);
            setPdfUrl(url);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const fetchTextFile = async (filePath) => {
        const response = await fetch(filePath);
        if (!response.ok) {
            throw new Error('Failed to fetch text file');
        }
        return await response.text();
    };

    const handleDownload = () => {
        const link = document.createElement('a');
        link.href = pdfUrl;
        link.download = 'compiled.pdf';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };

    return (
        <div>
            <h1>LaTeX Compiler</h1>
            <div>
                <label>
                    <input
                        type="radio"
                        value="mid"
                        checked={selectedOption === 'mid'}
                        onChange={handleOptionChange}
                    />
                    Mid Paper
                </label>
                <label>
                    <input
                        type="radio"
                        value="sem"
                        checked={selectedOption === 'sem'}
                        onChange={handleOptionChange}
                    />
                    Sem Paper
                </label>
            </div>
            <textarea
                rows="10"
                cols="50"
                value={latexCode}
                onChange={handleInputChange}
                placeholder="Enter LaTeX code here"
            />
            <br />
            <button onClick={handleCompile}>Compile LaTeX</button>
            {pdfUrl && (
                <div>
                    <h2>Compiled PDF</h2>
                    <button onClick={handleDownload}>Download PDF</button>
                    <br /><br />
                    <iframe src={pdfUrl} width="100%" height="600px" title="PDF Preview"></iframe>
                </div>
            )}
        </div>
    );
};

export default SurveyFormPage;
