import React, { useState } from 'react';
import axios from 'axios';
import './upload.css';

const UploadPage = ({ setLatexCode }) => { // Accept setLatexCode as a prop to update LaTeX in SurveyFormPage.js
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [queryText, setQueryText] = useState('');
    const [rawText, setRawText] = useState('');
    const [latexTemplate, setLatexTemplate] = useState('');

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const fetchLatexTemplate = async (templateName) => {
        try {
            const response = await fetch(`/${templateName}.txt`); // Fetch template file from public folder
            const templateData = await response.text();
            setLatexTemplate(templateData); // Store template data
        } catch (error) {
            console.error('Error fetching LaTeX template:', error);
        }
    };

    const handleUpload = async () => {
        if (!file) return;

        setLoading(true);
        const formData = new FormData();
        formData.append('file', file);
        formData.append('queryText', queryText);
        formData.append('latexTemplate', latexTemplate);

        try {
            const response = await axios.post('http://localhost:3322/upload', formData);
            const modifiedLatex = response.data.modifiedLatex; // Modified LaTeX from server response
            setLatexCode(modifiedLatex); // Send modified LaTeX back to SurveyFormPage.js
        } catch (error) {
            console.error('Error uploading file:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleQuery = async () => {
        if (!rawText) return;

        setLoading(true);

        try {
            const response = await axios.post('http://localhost:3322/query', { rawText, queryText, latexTemplate });
            const modifiedLatex = response.data.modifiedLatex; // Modified LaTeX from server response
            setLatexCode(modifiedLatex); // Send modified LaTeX back to SurveyFormPage.js
        } catch (error) {
            console.error('Error querying text:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleQueryTextChange = (e) => {
        setQueryText(e.target.value);
    };

    const handleRawTextChange = (e) => {
        setRawText(e.target.value);
    };

    return (
        <div className="upload-page-container">
            <div className="upload-form-container">
                <input type="file" onChange={handleFileChange} className="upload-input" />
                <button onClick={handleUpload} className="upload-button" disabled={loading}>
                    {loading ? 'Uploading...' : 'Upload'}
                </button>
                <input
                    type="text"
                    value={queryText}
                    onChange={handleQueryTextChange}
                    placeholder="Enter prompt for Gemini AI"
                    className="upload-query-input"
                />
                <textarea
                    value={rawText}
                    onChange={handleRawTextChange}
                    placeholder="Enter raw text to query"
                    className="upload-raw-text-input"
                />
                <button onClick={handleQuery} className="upload-button" disabled={loading}>
                    {loading ? 'Querying...' : 'Get Query'}
                </button>
            </div>
        </div>
    );
};

export default UploadPage;
