import React, { useState, useEffect } from 'react';

const SurveyFormPage = ({ latexCode: initialLatexCode }) => {
    const [latexCode, setLatexCode] = useState(initialLatexCode || '');
    const [pdfUrl, setPdfUrl] = useState(null);
    const [selectedFile, setSelectedFile] = useState('');

    useEffect(() => {
        setLatexCode(initialLatexCode);
    }, [initialLatexCode]);

    const handleInputChange = (event) => {
        setLatexCode(event.target.value);
        setPdfUrl(null);
    };

    const handleCompile = async () => {
        try {
            const response = await fetch('https://tinyurl.com/cmpillatex', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ latex: latexCode }),
            });

            if (!response.ok) {
                throw new Error('Failed to compile LaTeX');
            }

            const blob = await response.blob();
            const url = URL.createObjectURL(blob);
            setPdfUrl(url);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const handleDownload = () => {
        const link = document.createElement('a');
        link.href = pdfUrl;
        link.download = 'compiled.pdf';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };

    const readFileContent = async (filePath) => {
        try {
            const response = await fetch(filePath);
            if (!response.ok) {
                throw new Error('Failed to read file');
            }
            const text = await response.text();
            setLatexCode(text);
            setPdfUrl(null);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const handleFileSelection = (event) => {
        const selectedFile = event.target.value;
        setSelectedFile(selectedFile);
        const filePath = selectedFile === 'midqp' ? '/midqp.txt' : '/semqp.txt';
        readFileContent(filePath);
    };

    return (
        <div>
            <h1>LaTeX Compiler</h1>
            <textarea
                rows="10"
                cols="50"
                value={latexCode}
                onChange={handleInputChange}
                placeholder="Enter LaTeX code here"
            />
            <br />
            <button onClick={handleCompile}>Compile LaTeX</button>
            <br />
            <input
                type="radio"
                id="midqp"
                name="file"
                value="midqp"
                checked={selectedFile === 'midqp'}
                onChange={handleFileSelection}
            />
            <label htmlFor="midqp">Load midqp.txt</label>
            <br />
            <input
                type="radio"
                id="semqp"
                name="file"
                value="semqp"
                checked={selectedFile === 'semqp'}
                onChange={handleFileSelection}
            />
            <label htmlFor="semqp">Load semqp.txt</label>
            <br />
            {pdfUrl && (
                <div>
                    <h2>Compiled PDF</h2>
                    <button onClick={handleDownload}>Download PDF</button>
                    <br /><br />
                    <iframe src={pdfUrl} width="100%" height="600px" title="PDF Preview"></iframe>
                </div>
            )}
        </div>
    );
};

export default SurveyFormPage;
